import { IDeleteReq, IDownloadReq, IDownloadResp, IGetFileByIDReq, IFileListReq, IFileListResp, IPreSignReq, IPreSignResp, IUploadReq, IFileDesc } from "../structs/structs";
export interface IFileRequest {
    /**
     * 文件上传预加签
     */
    preSign(req: IPreSignReq): Promise<IPreSignResp>;
    /**
     * 文件上传
     */
    upload(req: IUploadReq): Promise<void>;
    /**
     * 文件下载
     */
    download(req: IDownloadReq): Promise<IDownloadResp>;
    /**
     * 文件删除
     */
    delete(req: IDeleteReq): Promise<void>;
    /**
     * 文件列表
     */
    list(req: IFileListReq): Promise<IFileListResp>;
    /**
     * 文件列表
     */
    getFileByID(req: IGetFileByIDReq): Promise<IFileDesc>;
}
